            local MASKED=$(echo "$VALUE" | sed -E 's/./*/g')
            __debug "Dotenv: ${NS^^}_${KEY}=$MASKED - ${FILE}"
        done <<<"$CONTENT"
        JSON="${JSON%,}"
        JSON="$JSON}"
        echo "$JSON"
        return 0
    }
